trigger.send:
local xpCurrent = %2 local xpOld = tonumber(GetVariable("xpOld")) local xpDiff = xpCurrent - xpOld local xpHourRoutine = tonumber(GetVariable("xpHourRoutine")) local xpAccum = tonumber(GetVariable("xpAccum"))
if xpDiff ~= 0 then SetVariable("xpDiff", xpDiff) if xpOld == -1 then Note ('initializing xp') else ColourNote ( "gray", "black", "You have gained xp: ", "cornflowerblue", "black", xpDiff) SetStatus ("You have gained xp: ", xpDiff)
if xpHourRoutine ~= 0 then xpAccum = xpAccum + xpDiff SetVariable("xpAccum", xpAccum)
DisplayXP () DisplayTimeRemaining () end
end
SetVariable("xpOld", xpCurrent) end

local xpCurrent = %3 local xpOld = tonumber(GetVariable("xpOld")) local xpDiff = xpCurrent - xpOld local xpHourRoutine = tonumber(GetVariable("xpHourRoutine")) local xpAccum = tonumber(GetVariable("xpAccum"))
if xpDiff ~= 0 then SetVariable("xpDiff", xpDiff) if xpOld == -1 then Note ('initializing xp') else ColourNote ( "gray", "black", "You have gained xp: ", "cornflowerblue", "black", xpDiff) SetStatus ("You have gained xp: ", xpDiff)
if xpHourRoutine ~= 0 then xpAccum = xpAccum + xpDiff SetVariable("xpAccum", xpAccum)
DisplayXP () DisplayTimeRemaining () end
end
SetVariable("xpOld", xpCurrent) end
