trigger.send:
local xpCurrent = %2 local xpOld = tonumber(GetVariable("xpOld")) local xpDiff = xpCurrent - xpOld local xpHourRoutine = tonumber(GetVariable("xpHourRoutine")) local xpAccum = tonumber(GetVariable("xpAccum")) local t = GetWorld ("xpHour") -- get "xpHour" world
if xpDiff ~= 0 then if xpOld == -1 then Note ('initializing xp') else ColourNote ( "gray", "black", "You have gained xp: ", "cornflowerblue", "black", xpDiff) SetStatus ("You have gained xp: ", xpDiff)
if xpHourRoutine ~= 0 then xpAccum = xpAccum + xpDiff SetVariable("xpAccum", xpAccum)
t:DeleteOutput() t:ColourNote ( "gray", "black", "You have gained xp: ", "cornflowerblue", "black", xpDiff) t:ColourNote ( "#C0C0C0", "black", "Accumulated xp: ", "cyan", "black", xpAccum) end
end
SetVariable("xpOld", xpCurrent) end
